package newjeans.bunnies.newjeansbunnies.global.security.jwtimport org.springframework.boot.context.properties.ConfigurationProperties@ConfigurationProperties(prefix = "jwt")class JwtProperties(    secretKey: String,    accessExp: Long,    refreshExp: Long) {    val accessExp: Long = accessExp * 60    val refreshExp: Long = refreshExp * 60    val key = secretKey}